mapper.select:
SELECT NO, ID, PW, NAME, PHONE, EMAIL, ADDRESS, REGDATE, STATUS
  FROM MEMBER
 WHERE ID = #{id} AND PW = #{pw}

SELECT COUNT(NO)
  FROM MEMBER
 WHERE ID = #{id}

SELECT COUNT(NO)
  FROM MEMBER
 WHERE PHONE = #{phone}

SELECT COUNT(NO)
  FROM MEMBER
 WHERE PHONE = #{phone} AND NO = #{no}
